mapper.select:
select scp.post_id, scp.community_code , scp.title , scp."content" , scp.reg_dt , scp.upd_dt , scp.writer_site_code , scp.writer_puuid, scd.game_mode , scd.is_open_top, scd.is_open_jungle, scd.is_open_mid, scd.is_open_ad, scd.is_open_sup, scd.post_status, scd.is_use_mic, scpp.top_puuid, scpp.jungle_puuid, scpp.mid_puuid, scpp.ad_puuid, scpp.sup_puuid from site_community_post scp inner join site_community_party scd on (scp.post_id = scd.post_id) inner join site_community_party_participant scpp on (scp.post_id = scpp.post_id) where scp.del_dt is null and scp.community_code = 'party'
and game_mode = #{gameMode}
and position_self = #{positionSelf.code}
and position_find = #{positionFind}
order by scp.post_id desc offset #{offset} limit #{limit}

