mapper.select:
select scp.post_id, scp.community_code , scp.title , scp."content" , scp.reg_dt , scp.upd_dt , scp.writer_site_code , scp.writer_puuid, scd.game_mode , scd.is_open_top, scd.is_open_jungle, scd.is_open_mid, scd.is_open_ad, scd.is_open_sup, scd.post_status, scd.is_use_mic, scd.tier_min, scd.tier_max, scpp.top_puuid, scpp.jungle_puuid, scpp.mid_puuid, scpp.ad_puuid, scpp.sup_puuid from site_community_post scp inner join site_community_party scd on (scp.post_id = scd.post_id) inner join site_community_party_participant scpp on (scp.post_id = scpp.post_id) where scp.del_dt is null and scp.community_code = 'party' and scd.post_status = 'OPEN' and scd.del_dt is null
and game_mode = #{gameMode}
and position_self = #{positionSelf.code}
and position_find = #{positionFind}
order by scp.post_id desc offset #{offset} limit #{limit}

mapper.insert:
INSERT INTO site_community_party_participant (
    post_id

        , top_puuid


        , jungle_puuid


        , mid_puuid


        , ad_puuid


        , sup_puuid

) VALUES (
    #{postId}

        , #{topPuuid}


        , #{junglePuuid}


        , #{midPuuid}


        , #{adPuuid}


        , #{supPuuid}

)

INSERT INTO site_community_party_applicant (
    post_id, applicant_puuid, position, applicant_seq, apply_status
) VALUES (
    #{postId}, #{applicantPuuid}, #{position}, #{applicantSeq}, #{applyStatus}
)

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM site_community_post
    WHERE
        post_id = #{postId}
        and writer_puuid = #{puuid}
)

SELECT EXISTS (
SELECT 1
FROM site_community_party_participant
WHERE
    post_id = #{postId}
    and (
        top_puuid = #{puuid}
        or jungle_puuid = #{puuid}
        or mid_puuid = #{puuid}
        or ad_puuid = #{puuid}
        or sup_puuid = #{puuid}
    )
)

mapper.update:
UPDATE site_community_party_applicant
SET apply_status = #{applyStatus}, upd_dt = current_timestamp
WHERE
    post_id = #{postId}
    and applicant_seq = #{applicantSeq}

mapper.select:
SELECT EXISTS (
    SELECT 1
   FROM site_community_post scp
    inner join site_community_party scd on (scp.post_id = scd.post_id)
    inner join site_community_party_participant scpp on (scp.post_id = scpp.post_id)
    WHERE scp.del_dt is null
         and scd.post_status = 'OPEN'
         and (
           top_puuid = #{puuid}
               or jungle_puuid = #{puuid}
               or mid_puuid = #{puuid}
               or ad_puuid = #{puuid}
               or sup_puuid = #{puuid}
           )
)

mapper.update:
UPDATE site_community_party_participant
SET upd_dt = current_timestamp

        , top_puuid = #{topPuuid}


        , jungle_puuid = #{junglePuuid}


        , mid_puuid = #{midPuuid}


        , ad_puuid = #{adPuuid}


        , sup_puuid = #{supPuuid}

WHERE
    post_id = #{postId}

UPDATE site_community_party_applicant
SET del_dt = current_timestamp
WHERE
    post_id = #{postId}
    AND applicant_puuid = #{applicantPuuid}
    AND applicant_seq != #{applicantSeq}

mapper.select:
select
    count(*)
from
    site_community_post scp
        inner join site_community_party scp2 on (scp.post_id = scp2.post_id)
        inner join site_community_party_participant scpp on (scp.post_id = scpp.post_id)
where
    scp.del_dt is null
  and scp2.del_dt is null
  and scp.writer_puuid != #{puuid}
  and (
    scpp.top_puuid = #{puuid}
        or scpp.jungle_puuid = #{puuid}
        or scpp.mid_puuid = #{puuid}
        or scpp.ad_puuid = #{puuid}
        or scpp.sup_puuid = #{puuid}
    )

mapper.update:
UPDATE public.site_community_party_participant
SET
    top_puuid = CASE WHEN top_puuid = #{puuid} THEN NULL ELSE top_puuid END,
    jungle_puuid = CASE WHEN jungle_puuid = #{puuid} THEN NULL ELSE jungle_puuid END,
    mid_puuid = CASE WHEN mid_puuid = #{puuid} THEN NULL ELSE mid_puuid END,
    ad_puuid = CASE WHEN ad_puuid = #{puuid} THEN NULL ELSE ad_puuid END,
    sup_puuid = CASE WHEN sup_puuid = #{puuid} THEN NULL ELSE sup_puuid END,
    upd_dt = CURRENT_TIMESTAMP
WHERE post_id = #{postId}
  AND (
      top_puuid = #{puuid}
      OR jungle_puuid = #{puuid}
      OR mid_puuid = #{puuid}
      OR ad_puuid = #{puuid}
      OR sup_puuid = #{puuid}
  )
