mapper.insert:
INSERT INTO lol_summoner (
     puuid
     , summoner_id
     , account_id
     , summoner_level
     , external_mod_dt
     , profile_icon_id
) VALUES (
    #{puuid}
    , #{summonerId}
    , #{accountId}
    , #{summonerLevel}
    , #{externalModDt}
    , #{profileIconId}
)

mapper.update:
UPDATE lol_summoner
SET
     summoner_id = #{summonerId}
     , account_id = #{accountId}
     , summoner_level = #{summonerLevel}
     , external_mod_dt = #{externalModDt}
     , profile_icon_id = #{profileIconId}
WHERE
    puuid = #{puuid}
