mapper.update:
UPDATE site_notification
SET read_yn = true
WHERE
    target_site_code = #{siteCode}
	and notification_id = #{id}
    and read_dt = now()

update.foreach:
			UPDATE site_notification
			SET read_yn = true,
			    read_dt = now()
			WHERE
                target_site_code = #{siteCode}
                and notification_id = #{id}
