mapper.update:
UPDATE site_notification
SET read_yn = true,
	read_dt = now()
WHERE
    target_puuid = #{puuid}
	and notification_id = #{id}

update.foreach:
			UPDATE site_notification
			SET read_yn = true,
			    read_dt = now()
			WHERE
                target_puuid = #{puuid}
                and notification_id = #{id}
