mapper.select:
SELECT r.summoner_review_seq, r.reviewer_site_code, r.reviewer_puuid, r.target_puuid, r.comment, r.reg_dt, r.is_up, ssrt.tag_code, srt.tag_value, srt.tag_description, srt.tag_updown, targetLolInfo.profile_icon_id as targetProfileIconId, targetRiotInfo.game_name as targetGameName, targetRiotInfo.tag_line as targetTagLine FROM site_summoner_review r LEFT OUTER JOIN site_summoner_review_tag ssrt ON r.summoner_review_seq = ssrt.summoner_review_seq LEFT OUTER JOIN site_review_tag srt ON ssrt.tag_code = srt.tag_code LEFT OUTER JOIN riot_account targetRiotInfo ON targetRiotInfo.puuid = r.target_puuid LEFT OUTER JOIN lol_summoner targetLolInfo ON targetLolInfo.puuid = r.target_puuid WHERE r.del_dt IS NULL
and r.target_puuid = #{targetPuuid}
LIMIT #{limit}

mapper.update:
UPDATE site_summoner_review
SET is_up = #{isUp}
    , comment = #{comment}
    , match_id = #{matchId}
    , is_anonymous = #{isAnonymous}
WHERE
    summoner_review_seq = #{summonerReviewSeq}

mapper.select:
select count(*)
from site_summoner_review ssrreviewWithTagsMap
where
    reviewer_puuid = #{reviewerPuuid}
    and target_puuid = #{targetPuuid}
    and del_dt is null
