mapper.select:
SELECT lc.name_us, lc.name_kr, COALESCE( COUNT( DISTINCT CASE WHEN
THEN lmp.match_id END ), 0 ) as playCount, COALESCE( COUNT( DISTINCT CASE WHEN
THEN ssr.summoner_review_seq END ), 0 ) as reviewCount, COALESCE( COUNT( DISTINCT CASE WHEN
AND ssr.is_up = true THEN ssr.summoner_review_seq END ), 0 ) as upCount FROM lol_champion lc LEFT OUTER JOIN lol_match_participant lmp ON ( lc.name_us = lmp.champ_name
AND lmp.position = #{matchPosition}
) LEFT OUTER JOIN lol_match lm ON ( lmp.match_id = lm.match_id AND lm.queue_id = #{matchGameMode.queueId} ) LEFT OUTER JOIN lol_summoner_league lsl ON ( lmp.summoner_id = lsl.summoner_id
AND lsl.tier = #{tier}
) LEFT OUTER JOIN site_summoner_review ssr ON ( lmp.puuid = ssr.target_puuid AND ssr.del_dt IS NULL ) GROUP BY lc.name_us, lc.name_kr ORDER BY playCount DESC

WITH ChampionTags AS ( SELECT lc.name_us, rt.tag_value, COUNT(*) as tag_count, ROW_NUMBER() OVER (PARTITION BY lc.name_us ORDER BY COUNT(*) DESC) as rn FROM lol_champion lc LEFT OUTER JOIN lol_match_participant lmp ON ( lc.name_us = lmp.champ_name
AND lmp.position = #{matchPosition}
) LEFT OUTER JOIN lol_match lm ON ( lmp.match_id = lm.match_id AND lm.queue_id = #{matchGameMode.queueId} ) LEFT OUTER JOIN lol_summoner_league lsl ON ( lmp.summoner_id = lsl.summoner_id
AND lsl.tier = #{tier}
) LEFT OUTER JOIN site_summoner_review ssr ON ( lmp.puuid = ssr.target_puuid AND ssr.del_dt IS NULL ) LEFT OUTER JOIN site_summoner_review_tag srt ON ssr.summoner_review_seq = srt.summoner_review_seq LEFT OUTER JOIN site_review_tag rt ON srt.tag_code = rt.tag_code WHERE rt.tag_value IS NOT NULL AND GROUP BY lc.name_us, rt.tag_value ) SELECT name_us, STRING_AGG(tag_value, ',') as top_tags FROM ChampionTags WHERE rn <= 3 GROUP BY name_us

SELECT lc.name_us, lc.name_kr, STRING_AGG(DISTINCT rt.tag_value,',') as top_tags COALESCE( COUNT(DISTINCT CASE WHEN lm.queue_id = #{matchGameMode.queueId}
AND lmp.position = #{matchPosition}
AND lsl.tier = #{tier}
THEN ssr.summoner_review_seq END ), 0 ) as review_count, FROM lol_champion lc LEFT OUTER JOIN lol_match_participant lmp ON ( lc.name_us = lmp.champ_name
AND lmp.position = #{matchPosition}
) LEFT OUTER JOIN lol_match lm ON ( lmp.match_id = lm.match_id AND lm.queue_id = #{matchGameMode.queueId} ) LEFT OUTER JOIN lol_summoner_league lsl ON ( lmp.summoner_id = lsl.summoner_id
AND lsl.tier = #{tier}
) LEFT OUTER JOIN site_summoner_review ssr ON ( lmp.puuid = ssr.target_puuid AND ssr.del_dt IS NULL ) LEFT OUTER JOIN site_summoner_review_tag srt ON ssr.summoner_review_seq = srt.summoner_review_seq LEFT OUTER JOIN site_review_tag rt ON srt.tag_code = rt.tag_code GROUP BY lc.name_us, lc.name_kr ORDER BY review_count DESC
